import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import moment from "moment";
import Parser from "html-react-parser";

import { IConClose, SharedButton } from "@components";
import { selectMerchant } from "@redux";
import { IBenefit, ICoupon, IReward } from "@interfaces";

interface IProps {
    handleCloseDetail: () => void;
    data: IBenefit | IReward | ICoupon;
}

export const ComponentHistoryDetail = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //redux states
    const { themeColor } = useSelector(selectMerchant);
    //page redux
    const { handleCloseDetail, data } = props;
    //page variable
    const dateFormat = moment(data.updatedAtHistory).format("DD/MM/YYYY h:mm:ss a");

    return (
        <StyledContainer>
            <div className="content">
                <div className="close" onClick={handleCloseDetail}>
                    <IConClose />
                </div>
                <div className="confirmation">{t("page.history.confirmation")}</div>
                <div className="title">{t("page.history.name")}</div>
                <div className="desc">{data.name}</div>
                <div className="title">{t("page.history.item")}</div>
                {data?.guideline && <div className="desc">{Parser(data?.guideline)}</div>}
                <div className="title">{t("page.history.ticket")}</div>
                {data?.code && <div className="desc">{data?.code}</div>}
                <div className="title">{t("page.history.date_time")}:</div>
                <div className="desc">{dateFormat}</div>
                <div className="title desConfirm">{t("page.history.desConfirm")}</div>
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleCloseDetail}
                >
                    {t("page.history.confirm")}
                </StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    display: flex;
    /* text-align: center; */
    align-items: center;
    justify-content: center;
    max-width: 480px;
    background: rgba(0, 0, 0, 0.5);
    .content {
        width: 90%;
        padding: 28px;
        background-color: #fff;
        position: relative;
        border-radius: 8px;
        .close {
            position: absolute;
            top: 20px;
            right: 20px;
            cursor: pointer;
        }
    }
    .confirmation {
        font-weight: 600;
        font-size: 18px;
        line-height: 24px;
        color: #000000;
        padding-bottom: 14px;
        border-bottom: 1px solid #707070;
    }
    .title {
        margin: 24px 0 4px 0;
        font-weight: bold;
        font-size: 14px;
        line-height: 19px;
        color: #989898;
    }
    .desc {
        font-weight: 600;
        font-size: 16px;
        color: #000000;
    }
    .desConfirm {
        width: 90%;
        margin: 24px auto 4px auto;
        text-align: center;
    }
`;

const StyledButton = styled(SharedButton)`
    width: 100%;
    margin: 46px auto 20px auto;
`;
