import { useFormik, FormikHelpers } from "formik";
import { useDispatch } from "react-redux";
import { useState } from "react";
import { CountryData } from "react-intl-tel-input";
import { useTranslation } from "react-i18next";
import { useHistory } from "react-router";

import { ComponentSignInTel } from "@components";
import { IAuth, IPhone } from "@interfaces";
import { authApi } from "@api";
import { setAuth, setExternalLogin, setLoading } from "@redux";
import { useNotify } from "@utils";
import { enumExternalMethod } from "@configs";

export const ModuleTelSignIn = () => {
    //page hooks
    const dispatch = useDispatch();
    const { error, success } = useNotify();
    const { t } = useTranslation();
    const history = useHistory();
    //page state
    const [errorPhone, setError] = useState<string>();

    //page variable
    const initialValues: IPhone = {
        phoneNumber: "",
    };

    const handleRegister = async (values: IPhone, { setSubmitting }: FormikHelpers<IPhone>) => {
        if (errorPhone) return;

        if (!values.phoneNumber) {
            setError(t("validation.isNumber"));
            return;
        }

        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.loginPhone({
                phoneNumber: values.phone || "",
            });
            const data = res.data as IAuth;
            if (data) {
                dispatch(setExternalLogin({ type: enumExternalMethod.TEL, isSignUp: false }));
                dispatch(setAuth(data));
                success(t("message.login.success"));
                history.push(PATH_SIGN_UP_OTP);
            }
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };

    const { values, handleSubmit, isSubmitting, setFieldTouched, setFieldValue } = useFormik({
        initialValues: initialValues,
        onSubmit: handleRegister,
    });

    const handleChangePhoneNumber = (
        isValid: boolean,
        value: string,
        selectedCountryData: CountryData,
        fullNumber: string
    ) => {
        if (!isValid) setError(t("validation.isNumber"));
        else {
            setError("");
        }
        setFieldValue("phoneNumber", value);
        setFieldValue("phone", fullNumber.replace(/(\s|-)/g, ""));
    };

    const handleSelectFlag = (currentNumber: string, selectedCountryData: CountryData) => {
        if (currentNumber) {
            setFieldValue("phoneNumber", currentNumber);
        } else {
            setFieldValue("phoneNumber", "+" + selectedCountryData.dialCode);
        }
    };

    return (
        <ComponentSignInTel
            values={values}
            handleSubmit={handleSubmit}
            isSubmitting={isSubmitting}
            setFieldTouched={setFieldTouched}
            setFieldValue={setFieldValue}
            handleChangePhoneNumber={handleChangePhoneNumber}
            errorPhone={errorPhone}
            handleSelectFlag={handleSelectFlag}
        />
    );
};
