import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import { FormikErrors } from "formik";
import styled from "styled-components";

import { SharedButton, SharedIntlTelInput } from "@components";
import { selectMerchant } from "@redux";
import { IPhone } from "@interfaces";
import { memo } from "react";

interface IProps {
    values: IPhone;
    errorPhone: string;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
}

export const ComponentSignInTel = memo((props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);

    //page props
    const {
        values,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        setFieldValue,
        errorPhone,
        setError,
    } = props;

    return (
        <form onSubmit={handleSubmit}>
            <SharedIntlTelInput
                setFieldTouched={setFieldTouched}
                setFieldValue={setFieldValue}
                value={values.phoneNumber || ""}
                errorPhone={errorPhone}
                setError={setError}
            />
            <StyledSharedButton
                size="large"
                bg_color={merchant.themeColor}
                disabled={isSubmitting}
                type="submit"
            >
                {t("page.auth.sign_in")}
            </StyledSharedButton>
        </form>
    );
});

const StyledSharedButton = styled(SharedButton)`
    margin-top: 15px;
`;
