import styled from "styled-components";
import InfiniteScroll from "react-infinite-scroll-component";
import { Spin } from "antd";

import { ICoupon, ICouponList } from "@interfaces";
import { CouponItem } from "./Item";
import { useTranslation } from "react-i18next";

interface IProps {
    data: ICouponList[] | ICoupon[];
    handleClickItem: (id: string) => void;
    handleLoadMore: () => void;
    loadingPagination: boolean;
    under: string;
}

export const ComponentCouponList = (props: IProps) => {
    //page hooks
    const { t } = useTranslation();
    //page props
    const { data, handleClickItem, handleLoadMore, loadingPagination, under } = props;

    return (
        <StyledContainerCouponList>
            <InfiniteScroll
                dataLength={data.length}
                next={handleLoadMore}
                hasMore={true}
                loader={
                    loadingPagination && (
                        <div className="example">
                            <Spin size="large" spinning={loadingPagination} />{" "}
                        </div>
                    )
                }
            >
                {data.map((item, index) => {
                    let dataCoupon;
                    if (under === t("page.home.coupon")) {
                        dataCoupon = item as ICouponList;
                    } else {
                        dataCoupon = item as ICoupon;
                    }
                    return (
                        <CouponItem
                            handleClickItem={handleClickItem}
                            data={
                                under === t("page.home.coupon")
                                    ? { ...item.qrtable, exchangedType: item.exchangedType }
                                    : { ...item }
                            }
                            key={index}
                        />
                    );
                })}
            </InfiniteScroll>
        </StyledContainerCouponList>
    );
};

const StyledContainerCouponList = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
    .example {
        margin: 20px 0;
        margin-bottom: 20px;
        padding: 30px 50px;
        text-align: center;
        border-radius: 4px;
    }
    .infinite-scroll-component {
        overflow: hidden !important;
    }
`;
