import { useDispatch } from "react-redux";

import { merchantApi } from "@api";
import { ComponentUserSetting } from "@components";
import { setLoading } from "@redux";

export const PageUserSetting = () => {
    //page hooks
    const dispatch = useDispatch();
    c;

    const handleUploadImage = async (e: React.ChangeEventHandler<HTMLInputElement> | undefined) => {
        dispatch(setLoading(true));
        //@ts-ignore
        const values = e.file;
        const formData = new FormData();
        formData.append("file", values);
        try {
            const res = await merchantApi.uploadImage(formData);
        } catch (err: any) {
            const mess = Array.isArray(err.response.data.errors)
                ? err.response.data.errors[0]
                : err.response.data.errors;
            error(mess);
        } finally {
            dispatch(setLoading(false));
        }
    };

    return (
        <>
            <ComponentUserSetting handleUploadImage={handleUploadImage} />
        </>
    );
};
