import styled from "styled-components";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";

import { ComponentHeaderAuth, ComponentSignUpOTP } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { enumExternalMethod, enumValidation, PATH_SIGN_UP_EMAIL } from "@configs";
import { authApi } from "@api";
export const PageSignUpOTP = () => {
    //page hook
    const history = useHistory();
    //page state
    const [otp, setOtp] = useState<string>("");
    //redux state
    const merchant = useSelector(selectMerchant);
    const { externalLogin } = useSelector(selectAuth);

    useEffect(() => {
        if (externalLogin !== enumExternalMethod.TEL) {
            history.push(PATH_SIGN_UP_EMAIL);
        }
    }, []);

    const handleChangeOtp = (otp: string) => {
        setOtp(otp);
    };

    const handleVerifyOtp = async () => {
        if (otp.length < enumValidation.LENTH_OTP) return;
        const phoneNumber = window.sessionStorage.getItem("phoneNumber") || "";
        try {
            const res = authApi.verifyPhone({
                phoneNumber,
                code: otp,
            });
        } catch {
            //
        }
    };

    return (
        <StyledContainer color={merchant.themeColor}>
            <ComponentHeaderAuth />
            <ComponentSignUpOTP
                handleVerifyOtp={handleVerifyOtp}
                handleChangeOtp={handleChangeOtp}
                otp={otp}
            />
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ color: string }>``;
