import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { Checkbox } from "antd";
import { FormikErrors } from "formik";

import { StyledSignUp, SharedSocialLine, SharedButton, SharedIntlTelInput } from "@components";
import { selectMerchant } from "@redux";
import { ModuleSocialMedia } from "@modules";
import { enumExternalAuthType } from "@configs";
import { IExternalAuthState, IPhone } from "@interfaces";

interface IProps {
    values: IPhone;
    errorPhone: string;
    isSubmitting: boolean;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    setFieldTouched: (
        field: string,
        touched?: boolean | undefined,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setFieldValue: (
        field: string,
        value: any,
        shouldValidate?: boolean | undefined
    ) => Promise<void> | Promise<FormikErrors<IPhone>>;
    setError: React.Dispatch<React.SetStateAction<string | undefined>>;
}

export const ComponentSignInTel = (props: IProps) => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    const state: IExternalAuthState = {
        type: enumExternalAuthType.LOGIN,
    };

    //page props
    const {
        values,
        isSubmitting,
        handleSubmit,
        setFieldTouched,
        setFieldValue,
        errorPhone,
        setError,
    } = props;

    return (
        <StyledSignUp>
            <form onSubmit={handleSubmit}>
                <StyledContainer>
                    <div className="title">
                        {t("page.auth.sign_in_to")} {merchant.name}
                    </div>
                    <div className="choose">{t("page.auth.choose_sign_in_method")}</div>
                    <ModuleSocialMedia state={state} />
                    <SharedSocialLine />
                    <SharedIntlTelInput
                        setFieldTouched={setFieldTouched}
                        setFieldValue={setFieldValue}
                        value={values.phoneNumber || ""}
                        isSubmitting={isSubmitting}
                        errorPhone={errorPhone}
                        setError={setError}
                    />
                    <StyledButton
                        size="large"
                        bg_color={merchant.themeColor}
                        disabled={isSubmitting}
                        type="submit"
                    >
                        {t("page.auth.sign_in")}
                    </StyledButton>
                    <div className="remember">
                        <Checkbox>{t("page.auth.remember_me")}</Checkbox>
                    </div>
                </StyledContainer>
            </form>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        margin-top: 16px;
        color: #646464;
    }
    .selected-dial-code {
        padding-left: 0 !important;
    }
    .selected-flag {
        display: flex !important;
    }
    .intl-tel-input .country-list {
        width: 100%;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
