import { Carousel } from "antd";
import styled from "styled-components";

const Cover = styled.div`
    position: relative;
    width: 100%;
    .ant-carousel {
        position: absolute;
        width: 100%;
        height: 100%;
    }
    .slick-slider {
        width: 100%;
        height: 100%;
    }
    .slick-slide > div {
        display: flex;
    }
    .image {
        position: relative;
        background-size: cover;
        background-position: top center;
        width: 100%;
    }
    .weakColor & .image {
        -webkit-filter: invert(100%);
        filter: invert(100%);
    }
    .content {
        position: relative;
        z-index: 9;
    }
`;

interface IProps {
    task: any;
    provided: any;
    isDragging?: any;
}

export const SharedTaskItem = ({ task, provided }: IProps) => (
    <div
        className={`shadow-sm bg-white mb-2 rounded`}
        css={`
            position: relative;
        `}
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}
    >
        <div
            style={{
                position: "absolute",
                top: "16px",
                left: 0,
                height: "20px",
                width: "2px",
            }}
            className={`bg-${task.color}`}
        />
        <div className="p-3">
            <p className="mb-1">{task.title}</p>
            <small className="text-muted d-block clearfix">{task.description}</small>
        </div>
        {task.images && (
            <Cover style={{ height: 150 }}>
                <Carousel className="overflow-hidden">
                    {task.images.map((image: string, index: number) => (
                        <div key={index}>
                            <div
                                key={index}
                                className="image rounded-bottom"
                                style={{
                                    backgroundImage: `url(${image})`,
                                    height: 150,
                                }}
                            />
                        </div>
                    ))}
                </Carousel>
            </Cover>
        )}
    </div>
);
