import { Switch } from "react-router";
import { BrowserRouter } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { ComponentAppRoute } from "@components";
import { routes } from "@configs";
import { IMerchant, IRoute } from "@interfaces";
import { ComponentToast } from "src/Components/Toast";
import { merchantApi } from "@api";
import {
    addConfigClient,
    getInfo,
    resetConfirm,
    selectAuth,
    selectConfigClient,
    selectMerchant,
    setAuth,
    setMainColor,
    setMerchant,
} from "@redux";

export const ModuleMain = () => {
    //page hooks
    const dispatch = useDispatch();
    //redux state
    const { _id } = useSelector(selectMerchant);
    const configClient = useSelector(selectConfigClient);
    const { auth } = useSelector(selectAuth);
    //page variable
    const token = auth?.accessToken;

    useEffect(() => {
        (async () => {
            if (_id) {
                const merchantConfig = await merchantApi.getMerchantConfig();
                const merchant = merchantConfig.data.merchant as IMerchant;
                if (merchant) {
                    dispatch(setMerchant(merchant));
                    dispatch(setMainColor(merchant.themeColor));
                }

                const index = configClient.findIndex((item) => item.merchantId === _id);
                if (index >= 0) {
                    dispatch(setAuth(configClient[index].token));
                } else {
                    dispatch(
                        addConfigClient({
                            merchantId: _id,
                        })
                    );
                    dispatch(setAuth(null));
                }
            }
        })();
        if (token) {
            dispatch(getInfo());
        }
        dispatch(resetConfirm());
    }, []);

    return (
        <BrowserRouter>
            <Switch>
                {routes.map((e: IRoute, key) => (
                    <ComponentAppRoute key={key} {...e} />
                ))}
            </Switch>
            <ComponentToast />
        </BrowserRouter>
    );
};
