import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { useSelector } from "react-redux";

import { BLANK_IMAGE_URL } from "@configs";
import { IReward } from "@interfaces";
import { IconStarCircleSmall } from "@components";
import { selectMerchant } from "@redux";

interface IProps {
    data: IReward;
    handleClick: (value: IReward) => void;
}

export const RewardItem = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    //page props
    const { data, handleClick } = props;
    //redux state
    const { themeColor } = useSelector(selectMerchant);

    return (
        <StyledContainer slot="container-end">
            <div className="starSmall">
                <IconStarCircleSmall color={themeColor} />
                <span>{data.code}</span>
            </div>
            <div className="image">
                <img src={data.imageUrl[0] || BLANK_IMAGE_URL} />
                {data.levelIds?.length ? (
                    <div className="silver_member">
                        <div className="line"></div>
                        <div className="text">{t("page.reward.SILVER_MEMBER")}</div>

                        <div className="line"></div>
                    </div>
                ) : (
                    <></>
                )}
            </div>
            <div className="desc" onClick={() => handleClick(data)}>
                {data.name}
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    width: 209px;
    background-color: #fff;
    margin: 0 24px 28px 0;
    border-radius: 6px;
    position: relative;
    .starSmall {
        position: absolute;
        top: 12px;
        left: 12px;
        padding: 6px 4px;
        background-color: #fff;
        z-index: 10;
    }
    .image {
        width: 100%;
        height: 209px;
        position: relative;
        img {
            width: 100%;
            object-fit: cover;
            height: 100%;
        }
        .silver_member {
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background: linear-gradient(0deg, rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3));
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 0 10px;
            cursor: none;
            .line {
                width: 23px;
                height: 1px;
                background-color: #fff;
            }
            .text {
                margin-left: 12px;
                flex: 1;
                color: #fff;
                font-weight: normal;
                font-size: 12px;
            }
        }
    }
    .desc {
        font-weight: normal;
        font-size: 15px;
        line-height: 1.5rem;
        margin-top: 8px;
        color: #000000;
        display: -webkit-box;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
        padding: 8px 12px 18px 12px;
        cursor: pointer;
    }
`;
