import { useFormik, FormikHelpers } from "formik";
import * as Yup from "yup";
import { useDispatch } from "react-redux";

import { ComponentSignUpTel } from "@components";
import { IPhone } from "@interfaces";
import { useYup } from "@validations";
import { authApi } from "@api";
import { setExternalLogin, setLoading } from "@redux";
import { useNotify } from "@utils";
import { enumExternalMethod } from "@configs";

export const ModuleTelSignUp = () => {
    //page hooks
    const { YubPhoneSignUp } = useYup();
    const dispatch = useDispatch();
    const { error } = useNotify();

    //page variable
    const initialValues: IPhone = {
        phoneNumber: "",
    };
    const registerSchema = Yup.object().shape(YubPhoneSignUp);

    const handleRegister = async (values: IPhone, { setSubmitting }: FormikHelpers<IPhone>) => {
        //
        try {
            setSubmitting(true);
            dispatch(setLoading(true));
            const res = await authApi.registerPhone(values);
            if (res.data.success) {
                dispatch(setExternalLogin({ type: enumExternalMethod.TEL }));
            }
        } catch (errors: any) {
            const { message } = errors.response.data;
            error(message);
        } finally {
            setSubmitting(false);
            dispatch(setLoading(false));
        }
    };
    const { values, handleChange, handleBlur, handleSubmit, isSubmitting, errors, touched } =
        useFormik({
            initialValues: initialValues,
            validationSchema: registerSchema,
            onSubmit: handleRegister,
        });
    return (
        <ComponentSignUpTel
            handleBlur={handleBlur}
            handleChange={handleChange}
            errors={errors}
            touched={touched}
            values={values}
            handleSubmit={handleSubmit}
            isSubmitting={isSubmitting}
        />
    );
};
