import { IButton } from "@interfaces";
import styled from "styled-components";



const SharedButton = styled.div<IButton>`
    ${(p)=> {
        if(p.size === "large") return "width:100%;";
        if(p.size === "default") return "padding: 14px 48px;";
    }}
`;
