import querystring from "querystring";
import { memo, useCallback } from "react";
import { useSelector } from "react-redux";
import liff from "@line/liff";

import { ComponentLineButton } from "@components";
import { LINE_ACCESS_URL, LINE_SCOPE, PATH_LINE_LOGIN } from "@configs";
import { IExternalAuthState } from "@interfaces";
import { selectMerchant } from "@redux";

interface IProps {
    state: IExternalAuthState;
}

export const ModuleLine = memo((props: IProps) => {
    //props
    const { state } = props;
    //redux page
    const { lineIntegration } = useSelector(selectMerchant);

    const handleLineLogin = useCallback(() => {
        // Build query string.
        liff.init({
            liffId: "1656727135-Nz6QzdKW", // Use own liffId
        });

        if (!liff.isLoggedIn()) {
            liff.login({ redirectUri: "https://localhost:3000/line-login" });
        }
        const query = querystring.stringify({
            response_type: "code",
            client_id: lineIntegration?.lineLoginApi?.channelID || "",
            state: JSON.stringify(state),
            redirect_uri: `${process.env.REACT_APP_WEB_URL}${PATH_LINE_LOGIN}`,
        });
        // Build the Line authorize URL.
        const lineUrl = `${LINE_ACCESS_URL}/authorize?scope=${LINE_SCOPE}&${query}`;

        // window.location.href = lineUrl;
        // Redirect to external URL.
    }, []);

    return <ComponentLineButton onClick={handleLineLogin} />;
});
