import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { Checkbox, Col, Row } from "antd";
import { useSelector } from "react-redux";
import { FormikErrors, FormikTouched } from "formik";

import {
    IHandleBlur,
    IHandleChange,
    IHandleChangeSelect,
    ISelect,
    ISocialMedia,
} from "@interfaces";
import { SharedButton, SharedCheckBox, SharedInput, SharedSelect } from "..";
import { selectMerchant } from "@redux";

interface IProps {
    days: ISelect[];
    onChangeDay: IHandleChangeSelect;
    onChangeMonth: IHandleChangeSelect;
    onChangeYear: IHandleChangeSelect;
    months: ISelect[];
    years: ISelect[];
    onChangeAdditionField: IHandleChangeSelect;
    values: ISocialMedia;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<ISocialMedia>;
    touched: FormikTouched<ISocialMedia>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
}

export const ComponentFromSocial = (props: IProps) => {
    const {
        days,
        months,
        years,
        onChangeDay,
        onChangeYear,
        onChangeMonth,
        onChangeAdditionField,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
    } = props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    return (
        <StyledContainer>
            <SharedInput
                placeholder={t("page.auth.name")}
                value={values.fullName}
                name="fullName"
                onBlur={handleBlur}
                onChange={handleChange}
                errors={errors.fullName}
                touched={touched.fullName}
            />
            <SharedInput
                placeholder={t("page.auth.tel")}
                value={values.tel}
                name="tel"
                onBlur={handleBlur}
                onChange={handleChange}
                errors={errors.tel}
                touched={touched.tel}
            />
            <SharedInput
                placeholder={t("page.auth.email")}
                value={values.tel}
                name="email"
                onBlur={handleBlur}
                onChange={handleChange}
                errors={errors.email}
                touched={touched.email}
            />
            <div className="day">
                <div className="title">{t("page.auth.birth_date")}</div>
                <Row gutter={16}>
                    <Col span={8}>
                        <SharedSelect data={days} onChange={onChangeDay} />
                    </Col>
                    <Col span={8}>
                        <SharedSelect data={months} onChange={onChangeMonth} />
                    </Col>
                    <Col span={8}>
                        <SharedSelect data={years} onChange={onChangeYear} />
                    </Col>
                </Row>
            </div>
            {/* <SharedSelect
                data={[]}
                onChange={onChangeAdditionField}
                placeholder="What fruits do you like"
            /> */}
            <div className="bottom">
                <SharedCheckBox
                    name="acceptTermsAndConditions"
                    value={values.acceptTermsAndConditions || false}
                    onChange={handleChange}
                    label={t("page.auth.accept_terms_conditions")}
                    errors={errors.acceptTermsAndConditions}
                    touched={touched.acceptTermsAndConditions}
                />
                <StyledButton size="small" bg_color={merchant.themeColor}>
                    {t("page.auth.sign_up")}
                </StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    padding: 11px;
    background-color: #fff;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;

    .title {
        margin-bottom: 8px;
    }
    .day {
        background-color: #f7f7f8;
        margin-bottom: 24px;
        padding: 11px;
    }
    .bottom {
        width: fit-content;
        margin: 0 auto;
        margin-top: 42px;
        span {
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
        }
        .ant-checkbox-wrapper {
            display: block;
        }
    }
    /* .ant-checkbox-checked .ant-checkbox-inner {
        background-color: transparent !important;
        border: none;
    }

    .ant-checkbox-checked::after {
        border: 1px solid #676767 !important;
    } */

    /* .ant-checkbox-checked .ant-checkbox-inner::after {
        border: 2px solid #676767 !important;
    } */
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
