import styled from "styled-components";

export const StyledWelcome = styled.div<{ themeColor: string }>`
    text-align: center;
    .welcome {
        font-weight: bold;
        font-size: 25px;
        line-height: 33px;
        color: ${(p) => p.themeColor};
        padding-top: 26px;
    }
    .desc {
        width: 80%;
        margin: 16px auto 48px auto;
        font-size: 18px;
        line-height: 1.5rem;
        text-align: center;
        color: #000000;
    }
`;
