import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory, useParams } from "react-router";
import { useTranslation } from "react-i18next";

import { couponApi, merchantApi } from "@api";
import { ComponentCouponDetail, ComponentPopUpConfirm } from "@components";
import { PATH_404, enumStatus, enumTypes, PATH_HOME, PATH_SIGN_IN_EMAIL } from "@configs";
import { ICoupon, IMerchant, IMerchantQRcode } from "@interfaces";
import {
    selectAuth,
    setExternalTypes,
    setLoading,
    setMainColor,
    setMerchant,
    setMerchantID,
} from "@redux";
import { useNotify } from "@utils";

export const PageCoupon = () => {
    //page hook
    const { t } = useTranslation();
    const dispatch = useDispatch();
    const { id, idParent } = useParams<{ id: string; idParent: string }>();
    const history = useHistory();
    const { error, success } = useNotify();
    //page redux
    const { auth, externalType } = useSelector(selectAuth);
    //page state
    const [coupon, setCoupon] = useState<ICoupon>();
    const [showConfirm, setShowConfirm] = useState<boolean>(false);
    //page variable
    const token = auth?.accessToken;

    useEffect(() => {
        useCoupon();
    }, []);

    const useCoupon = async () => {
        try {
            dispatch(setLoading(true));
            if (id) {
                if (token) {
                    const res = await couponApi.getPreviewCoupon(id);
                    const data = res.data as ICoupon;
                    setCoupon(data);
                    dispatch(
                        setExternalTypes({ type: externalType?.type || enumTypes.COUPON, id: "" })
                    );
                } else {
                    const res = await merchantApi.getCouponId(id);
                    const data = res.data as IMerchantQRcode;
                    dispatch(setMerchantID(data.merchantId));
                    const merchantConfig = await merchantApi.getMerchantConfig();
                    const merchant = merchantConfig.data.merchant as IMerchant;
                    dispatch(setMerchant(merchant));
                    dispatch(setMainColor(merchant.themeColor));
                    dispatch(setExternalTypes({ type: enumTypes.COUPON, id }));
                    history.push(PATH_SIGN_IN_EMAIL);
                }
            } else {
                history.push(PATH_404);
            }
        } catch {
            history.push(PATH_404);
        } finally {
            dispatch(setLoading(false));
        }
    };

    const handleReceiveCoupon = async () => {
        try {
            dispatch(setLoading(true));
            const res = await couponApi.receiveCoupon({
                objectId: coupon?._id || "",
                status: enumStatus.ACTIVE,
                type: enumTypes.COUPON,
            });
            if (res.data.message) {
                success(res.data.message);
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
            history.push(PATH_HOME);
        }
    };

    const handleRedeemedCoupon = async () => {
        try {
            if (idParent) {
                dispatch(setLoading(true));
                const res = await couponApi.redeemedCoupon({
                    objectId: coupon?._id || "",
                    status: enumStatus.ACTIVE,
                    type: enumTypes.COUPON,
                    id: idParent,
                });
                if (res.data.message) {
                    success(res.data.message);
                }
            } else {
                dispatch(setLoading(true));
                const res = await couponApi.redeemedCouponNotId(
                    {
                        objectId: coupon?._id || "",
                        status: enumStatus.ACTIVE,
                        type: enumTypes.COUPON,
                    },
                    id || ""
                );
                if (res.data.message) {
                    success(res.data.message);
                }
            }
        } catch (err: any) {
            const { message } = err.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.received.fail"));
            }
        } finally {
            dispatch(setLoading(false));
            history.push(PATH_HOME);
        }
    };

    return (
        <>
            {coupon && (
                <ComponentCouponDetail
                    handleRedeemedCoupon={handleRedeemedCoupon}
                    coupon={coupon}
                    handleReceiveCoupon={handleReceiveCoupon}
                />
            )}
            {showConfirm && coupon && <ComponentPopUpConfirm data={coupon as ICoupon} />}
        </>
    );
};
