import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectMerchant } from "@redux";
import { IConBecomeBenefit, IConBecomeCoupon, IConBecomeStar } from "@components";
import { ModuleMemberShipTier } from "@modules";

export const ComponentUserDetail = () => {
    //hooks
    const { t } = useTranslation();
    const { name, themeColor } = useSelector(selectMerchant);

    return (
        <>
            <StyledCart>
                <div className="become">{t("page.user_detail.become_member")}</div>
                <div className="desc">
                    {t("page.user_detail.fill_in_your_information")}
                    {name} {t("page.user_detail.and_receive")}
                </div>
                <div className="group">
                    <div className="item">
                        <IConBecomeStar color={themeColor} />
                        <span>{t("page.user_detail.star_desc")}</span>
                    </div>
                    <div className="item">
                        <IConBecomeCoupon color={themeColor} />
                        <span>{t("page.user_detail.coupon_desc")}</span>
                    </div>
                    <div className="item">
                        <IConBecomeBenefit color={themeColor} />
                        <span>{t("page.user_detail.benefit_desc")}</span>
                    </div>
                    <div className="item border-none">
                        <IConBecomeStar color={themeColor} />
                        <span>{t("page.user_detail.draws_desc")}</span>
                    </div>
                </div>
            </StyledCart>
            <StyledCart>
                <div className="title_rule">{t("page.user_detail.user_detail")}</div>
            </StyledCart>
            <ModuleMemberShipTier />
        </>
    );
};

const StyledCart = styled.div`
    width: 88%;
    margin: 0 auto;
    padding: 0 24px 32px 24px;
    background-color: #fff;
    box-shadow: rgb(204 204 204) 0px 0px 3px 2px;
    border-radius: 12px;
    margin-bottom: 20px;
    .become {
        text-align: center;
        padding-top: 36px;
        font-family: Segoe UI;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        margin-bottom: 24px;
    }
    .desc {
        text-align: center;
        font-size: 18px;
        color: #7e7e7e;
        line-height: 1.5rem;
        font-family: Leelawadee UI;
        font-style: normal;
        font-weight: normal;
        margin-bottom: 6px;
    }
    .group {
        .item {
            padding: 24px 0;
            border-bottom: 1px solid #737373;
            span {
                display: inline-block;
                margin-left: 20px;
                font-family: Segoe UI;
                font-style: normal;
                font-weight: normal;
                font-size: 18px;
                color: #000000;
            }
        }
    }
    .border-none {
        border-bottom: initial !important;
    }
`;
