import styled from "styled-components";
import InfiniteScroll from "react-infinite-scroll-component";
import { Spin } from "antd";

import { ICouponList } from "@interfaces";
import { CouponItem } from "./Item";

interface IProps {
    data: ICouponList[];
    handleClickItem: (id: string) => void;
    handleLoadMore: () => void;
    loadingPagination: boolean;
}

export const ComponentCouponList = (props: IProps) => {
    const { data, handleClickItem, handleLoadMore, loadingPagination } = props;

    return (
        <StyledContainerCouponList>
            <InfiniteScroll
                dataLength={data.length}
                next={handleLoadMore}
                hasMore={true}
                loader={
                    loadingPagination && (
                        <div className="example">
                            <Spin size="large" spinning={loadingPagination} />{" "}
                        </div>
                    )
                }
            >
                {data.map((item, index) => {
                    return (
                        <CouponItem
                            handleClickItem={handleClickItem}
                            data={item.qrtable}
                            key={index}
                        />
                    );
                })}
            </InfiniteScroll>
        </StyledContainerCouponList>
    );
};

const StyledContainerCouponList = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
    .example {
        margin: 20px 0;
        margin-bottom: 20px;
        padding: 30px 50px;
        text-align: center;
        border-radius: 4px;
    }
    .infinite-scroll-component {
        overflow: hidden !important;
    }
`;
