import { useTranslation } from "react-i18next";
import styled from "styled-components";
import { Checkbox, Col, Row } from "antd";
import { useSelector } from "react-redux";
import { FormikErrors, FormikTouched } from "formik";

import {
    IDate,
    IEmailForm,
    IHandleBlur,
    IHandleChange,
    IHandleChangeSelect,
    ISelect,
} from "@interfaces";
import { SharedButton, SharedInput, SharedSelect, StyledError } from "..";
import { selectMerchant } from "@redux";

interface IProps {
    days: ISelect[];
    onChangeDay: IHandleChangeSelect;
    onChangeMonth: IHandleChangeSelect;
    onChangeYear: IHandleChangeSelect;
    values: IEmailForm;
    handleBlur: IHandleBlur;
    handleChange: IHandleChange;
    errors: FormikErrors<IEmailForm>;
    touched: FormikTouched<IEmailForm>;
    handleSubmit: (e?: React.FormEvent<HTMLFormElement> | undefined) => void;
    isSubmitting: boolean;
    months: ISelect[];
    years: ISelect[];
    onChangeAdditionField: IHandleChangeSelect;
    date: IDate;
}

export const ComponentFromEmail = (props: IProps) => {
    const {
        days,
        months,
        years,
        onChangeDay,
        onChangeYear,
        onChangeMonth,
        values,
        handleBlur,
        handleChange,
        errors,
        touched,
        handleSubmit,
        isSubmitting,
        date,
    } = props;
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);
    return (
        <StyledContainer>
            <form onSubmit={handleSubmit}>
                <SharedInput
                    placeholder={t("page.auth.name")}
                    name="fullName"
                    value={values.fullName}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.fullName}
                    touched={touched.fullName}
                />
                <SharedInput
                    placeholder={t("page.auth.tel")}
                    name="tel"
                    value={values.tel}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.tel}
                    touched={touched.tel}
                />
                <SharedInput
                    placeholder={t("page.auth.password")}
                    type="password"
                    name="password"
                    value={values.password}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.password}
                    touched={touched.password}
                />
                <SharedInput
                    placeholder={t("page.auth.confirm_password")}
                    type="password"
                    name="conformPassword"
                    value={values.conformPassword}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    errors={errors.conformPassword}
                    touched={touched.conformPassword}
                />
                <div className="day">
                    <div className="title">{t("page.auth.birth_date")}</div>
                    <Row gutter={16}>
                        <Col span={8}>
                            <SharedSelect value={date.day} data={days} onChange={onChangeDay} />
                        </Col>
                        <Col span={8}>
                            <SharedSelect
                                value={date.month}
                                data={months}
                                onChange={onChangeMonth}
                            />
                        </Col>
                        <Col span={8}>
                            <SharedSelect value={date.year} data={years} onChange={onChangeYear} />
                        </Col>
                    </Row>
                    {date.error && (
                        <StyledError visible={true}>{t("validation.is_required")} </StyledError>
                    )}
                </div>
                {/* <SharedSelect
                    data={[]}
                    onChange={onChangeAdditionField}
                    placeholder="What fruits do you like"
                /> */}
                <div className="bottom">
                    <Checkbox
                        name="acceptTermsAndConditions"
                        value={values.acceptTermsAndConditions}
                        onChange={handleChange}
                    >
                        {t("page.auth.accept_terms_conditions")}
                    </Checkbox>
                    {errors.acceptTermsAndConditions && touched.acceptTermsAndConditions && (
                        <StyledError visible={true}>{errors.acceptTermsAndConditions}</StyledError>
                    )}
                    <StyledButton
                        size="small"
                        bg_color={merchant.themeColor}
                        disabled={isSubmitting}
                        type="submit"
                    >
                        {t("page.auth.sign_up")}
                    </StyledButton>
                </div>
            </form>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
    padding: 11px;
    background-color: #fff;
    color: #676767;
    text-align: start;
    font-weight: normal;
    font-size: 20px;
    line-height: 27px;

    .title {
        margin-bottom: 8px;
    }
    .day {
        background-color: #f7f7f8;
        margin-bottom: 24px;
        padding: 11px;
    }
    .bottom {
        text-align: center;
        margin-top: 42px;
        span {
            font-weight: normal;
            font-size: 16px;
            line-height: 21px;
            color: #676767;
        }
        .ant-checkbox-wrapper {
            display: block;
        }
    }
    /* .ant-checkbox-checked .ant-checkbox-inner {
        background-color: transparent !important;
        border: none;
    }

    .ant-checkbox-checked::after {
        border: 1px solid #676767 !important;
    } */

    /* .ant-checkbox-checked .ant-checkbox-inner::after {
        border: 2px solid #676767 !important;
    } */
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
