import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import { useEffect, useState } from "react";
import styled from "styled-components";

import { ComponentHeaderAuth, ComponentHome, StyledHome, ComponentQRcode } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { ModuleHistory, ModulesBenefit, ModulesCoupon, ModulesReward } from "@modules";

export const PageHome = () => {
    //page hook
    const { t } = useTranslation();
    //page redux
    const { userInfo } = useSelector(selectAuth);
    const { themeColor } = useSelector(selectMerchant);
    //page variable
    const navigation: Array<string> = [
        t("page.home.use_point"),
        t("page.home.benefits"),
        t("page.home.coupon"),
        t("page.home.history"),
    ];
    //page state
    const [under, setUnder] = useState<string>(navigation[0]);
    const [showQrCode, setShowQrCode] = useState<boolean>(false);

    const handleToggleUnder = (values: string) => {
        setUnder(values);
    };

    const handleToggleQrCode = () => {
        setShowQrCode(!showQrCode);
    };

    const render = () => {
        switch (under) {
            case t("page.home.coupon"):
                return <ModulesCoupon />;
                break;
            case t("page.home.use_point"):
                return <ModulesReward />;
                break;
            case t("page.home.benefits"):
                return <ModulesBenefit />;
                break;
            case t("page.home.history"):
                return <ModuleHistory />;
                break;
            default:
                return <></>;
                break;
        }
    };

    return (
        <>
            <ComponentHeaderAuth />
            <StyledHome mainColor={themeColor}>
                {!showQrCode && (
                    <>
                        <ComponentHome
                            under={under}
                            handleToggleUnder={handleToggleUnder}
                            userInfo={userInfo}
                            navigation={navigation}
                            themeColor={themeColor}
                            handleToggleQrCode={handleToggleQrCode}
                        />
                        <StyledContainerHome>{render()}</StyledContainerHome>
                    </>
                )}
                {showQrCode && <ComponentQRcode handleToggleQrCode={handleToggleQrCode} />}
            </StyledHome>
        </>
    );
};

const StyledContainerHome = styled.div`
    min-height: 80vh;
    background-color: #f7f7f8;
`;
