import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

import { rewardApi } from "@api";
import { ComponentRewardList } from "@components";
import { IReward } from "@interfaces";
import { setLoading } from "@redux";

interface IData {
    quantity: number;
    reward: IReward;
}

export const ModulesReward = () => {
    const dispatch = useDispatch();
    //page state
    const [reward, setReward] = useState<IData[]>([]);

    useEffect(() => {
        (async () => {
            dispatch(setLoading(true));
            const res = await rewardApi.getListReward();
            const data = res.data.docs as IReward[];
            //WHAT: xu ly data thang IData
            data.map((item) => item.quantity);
            setReward(data);
            dispatch(setLoading(false));
        })();
    }, []);

    return <>{reward && <ComponentRewardList />}</>;
};
