import Parser from "html-react-parser";
import styled from "styled-components";

import { IBenefit } from "@interfaces";
import { BLANK_IMAGE_URL } from "@configs";

interface IProps {
    data: IBenefit;
}

export const BenefitItem = (props: IProps) => {
    const { data } = props;
    return (
        <StyledContainer>
            <img src={data.imageUrl[0] || BLANK_IMAGE_URL} />
            <div className="content">
                <div className="name">{data.name}</div>
                {/* <div className="desc">{Parser(data.description)}</div> */}
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ themeColor: string }>`
    display: flex;
    margin-bottom: 28px;
    img {
        width: 170px;
        height: 170px;
        object-fit: cover;
    }
    .content {
        padding: 16px;
        background-color: #fff;
        flex: 1;
        .name {
            font-weight: bold;
            font-size: 16px;
            line-height: 1.5rem;
            color: #000000;
            margin-bottom: 16px;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
        }
        .desc {
            font-weight: normal;
            font-size: 14px;
            line-height: 1.5rem;
            color: #8a8a8a;
            display: -webkit-box;
            -webkit-line-clamp: 3;
            -webkit-box-orient: vertical;
            overflow: hidden;
            p {
                margin: initial;
                width: 100%;
                white-space: nowrap;
                overflow: hidden;
                text-overflow: ellipsis;
            }
        }
    }
`;
