import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import moment from "moment";
import Parser from "html-react-parser";

import { selectMerchant } from "@redux";
import { ICoupon } from "@interfaces";
import { enumUnit } from "@configs";
import { IConMoney } from "@components";

interface IProps {
    data: ICoupon;
    handleClickItem: (id: string) => void;
}

export const CouponItem = (props: IProps) => {
    //page props
    const { data, handleClickItem } = props;
    //page hook
    const { t } = useTranslation();
    //redux page
    const { themeColor } = useSelector(selectMerchant);

    const dateFormat = moment(data?.endDate).format("DD/MM/yyyy");

    return (
        <>
            <StyledContainerTop themeColor={themeColor}>
                <div className="right">
                    <div className="name">{data.name}</div>
                    <div className="desc">{Parser(data.guideline || "")}</div>
                </div>
                <div className="money">
                    <IConMoney />
                </div>
                <div className="left">
                    <div className="money_right">
                        {data.unit === enumUnit.PERCENTAGE
                            ? data.benefit + "%"
                            : data.benefit + "฿"}
                    </div>
                </div>
            </StyledContainerTop>
            <StyledContainerBottom themeColor={themeColor}>
                <div className="date">
                    {t("page.coupon.expiration_date")}: {dateFormat}
                </div>
                <div
                    className="recceive_coupon"
                    onClick={() => handleClickItem(data.qrToken || "")}
                >
                    {t("page.coupon.recceive_coupon")}&gt;
                </div>
            </StyledContainerBottom>
        </>
    );
};

const StyledContainerTop = styled.div<{ themeColor: string }>`
    background-color: #fff;
    padding: 0 24px 32px 24px;
    display: flex;
    justify-content: space-between;
    position: relative;
    .right {
        flex: 1;
        max-width: 50%;
        @media (max-width: ${(p) => p.theme.breakPoints.breakMobileLarge}) {
            max-width: 45%;
        }
        @media (max-width: ${(p) => p.theme.breakPoints.breakMobile}) {
            max-width: 42%;
        }
        * {
            padding: 0;
            margin: 0;
            box-sizing: border-box;
        }
        .name {
            font-family: "Segoe UI", "Roboto", sans-serif;
            max-width: 95%;
            font-weight: bold;
            font-size: 20px;
            line-height: 27px;
            color: #000000;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            padding-top: 18px;
        }
        .desc {
            max-width: 95%;
            font-weight: normal;
            font-size: 12px;
            line-height: 1.5rem;
            color: #8a8a8a;
            margin-top: 4px;
            display: -webkit-box;
            -webkit-line-clamp: 3;
            -webkit-box-orient: vertical;
            overflow: hidden;
            max-height: 4.5rem;
            * {
                font-family: "Segoe UI", "Roboto", sans-serif;
                font-style: normal;
                font-weight: normal;
                font-size: 12px;
                line-height: 1.2rem;
            }
        }
    }
    .money {
        width: fit-content;
    }
    .left {
        width: fit-content;
        display: -webkit-box;
        -webkit-line-clamp: 1;
        -webkit-box-orient: vertical;
        overflow: hidden;
        height: fit-content;
        .money_right {
            width: 100%;
            font-weight: bold;
            font-size: 30px;
            color: ${(p) => p.themeColor};
            text-align: start;
            padding-top: 18px;
            word-break: break-all;
        }
    }
    &::after {
        content: "";
        position: absolute;
        bottom: 0;
        left: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(-50%, 50%);
    }
    &::before {
        content: "";
        position: absolute;
        bottom: 0;
        right: 0;
        width: 30px;
        height: 30px;
        border-radius: 50%;
        background-color: #f7f7f8;
        transform: translate(50%, 50%);
    }
`;

const StyledContainerBottom = styled.div<{ themeColor: string }>`
    background-color: #fff;
    display: flex;
    justify-content: space-between;
    padding: 14px 24px 18px 24px;
    margin-bottom: 24px;
    font-family: "Leelawadee UI", "Roboto", sans-serif;
    .date {
        font-weight: normal;
        font-size: 12px;
        line-height: 16px;
        color: #8a8a8a;
    }
    .recceive_coupon {
        font-style: normal;
        font-weight: bold;
        font-size: 13px;
        color: ${(p) => p.themeColor};
        cursor: pointer;
    }
`;
