import styled from "styled-components";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation } from "swiper";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import { useTranslation } from "react-i18next";
import React from "react";

import { RewardItem } from "./Item";
import { IDataReward, IReward } from "@interfaces";
import { useWindowDimensions } from "@utils";

SwiperCore.use([Navigation]);

interface IProps {
    data: IDataReward[];
    handleClick: (value: IReward) => void;
}

export const ComponentRewardList = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    const { width } = useWindowDimensions();
    //page props
    const { data, handleClick } = props;

    const slidesPerView = width <= 414 ? (width <= 384 ? (width <= 320 ? 1.2 : 1.4) : 1.6) : 1.8;

    return (
        <>
            <StyledContainer>
                {data.map((item, index) => {
                    return (
                        <React.Fragment key={index}>
                            <div className="money">
                                {item.point}{" "}
                                {item.point > 1 ? t("page.reward.points") : t("page.reward.point")}
                            </div>
                            <Swiper slidesPerView={slidesPerView}>
                                {item.reward.map((values, index) => {
                                    return (
                                        <SwiperSlide key={index}>
                                            <RewardItem data={values} handleClick={handleClick} />
                                        </SwiperSlide>
                                    );
                                })}
                            </Swiper>
                        </React.Fragment>
                    );
                })}
            </StyledContainer>
        </>
    );
};

const StyledContainer = styled.div`
    width: 90%;
    margin: 0 auto;
    padding: 25px 0;
    position: relative;
    .money {
        font-style: normal;
        font-weight: 600;
        font-size: 16px;
        line-height: 1.5rem;
        color: #a7a7a7;
        margin-bottom: 12px;
        font-family: "Segoe UI", "Roboto", sans-serif;
    }
    .list-reward {
        display: flex;
    }
`;
