import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectMerchant } from "@redux";

export const ComponentUserDetail = () => {
    //hooks
    const { t } = useTranslation();
    const { name } = useSelector(selectMerchant);

    return (
        <StyledCart>
            <div className="become">{t("page.user_detail.become_member")}</div>
            <div className="desc">
                {t("page.user_detail.fill_in_your_information")}
                {name} {t("page.user_detail.and_receive")}
            </div>
        </StyledCart>
    );
};

const StyledCart = styled.div`
    width: 88%;
    margin: 0 auto;
    padding: 0 24px;
    background-color: #fff;
    box-shadow: rgb(204 204 204) 0px 0px 3px 2px;
    border-radius: 4px;

    .become {
        text-align: center;
        padding-top: 36px;
        font-family: Segoe UI;
        font-style: normal;
        font-weight: 600;
        font-size: 20px;
        color: #000000;
        margin-bottom: 24px;
    }
    .desc {
        text-align: center;
        font-size: 18px;
        color: #7e7e7e;
        line-height: 1.5rem;
        font-family: Leelawadee UI;
        font-style: normal;
        font-weight: normal;
    }
`;
