import { Select } from "antd";
import { useTranslation } from "react-i18next";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Navigation } from "swiper";
import "swiper/swiper-bundle.min.css";
import "swiper/swiper.min.css";
import "swiper/components/navigation/navigation.min.css";
import Parser from "html-react-parser";
import { useState } from "react";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { BLANK_IMAGE_URL } from "@configs";
import { IconStarSub, SharedButton, StyledPreview } from "@components";
import { IReward } from "@interfaces";
import { selectMerchant } from "@redux";

interface IProps {
    data: IReward;
    handleUseCoupon: () => void;
    handleAddCount: () => void;
    handleMinusCount: () => void;
    handleCalculatePoint: () => void;
}

const { Option } = Select;
SwiperCore.use([Navigation]);

export const ComponentRewardDetail = (props: IProps) => {
    //hook
    const { t } = useTranslation();
    // props
    const { data, handleUseCoupon } = props;
    //redux states
    const { themeColor } = useSelector(selectMerchant);
    // state
    const [count, setCount] = useState<number>(1);

    const handleAddCount = () => {
        if (count < (data.limit || 1)) {
            setCount(count + 1);
        }
    };
    const handleMinusCount = () => {
        if (count > 1) {
            setCount(count - 1);
        }
    };

    const handleCalculatePoint = () => {
        if (data.point) return data.point * count;
    };

    return (
        <StyledPreview>
            <div className="page_body">
                <div className="img-field">
                    <Swiper slidesPerView={1}>
                        {data.imageUrl.length ? (
                            data.imageUrl.map((item, index) => (
                                <SwiperSlide key={index}>
                                    <div className="img-wrap">
                                        <img src={item} />
                                    </div>
                                </SwiperSlide>
                            ))
                        ) : (
                            <SwiperSlide>
                                <div className="img-wrap">
                                    <img className="empty-img" src={BLANK_IMAGE_URL} />
                                </div>
                            </SwiperSlide>
                        )}
                    </Swiper>
                </div>
                <div className="name-field">
                    <p>
                        {t("page.reward.quantity_available", {
                            returnObjects: true,
                            number: data.quantity,
                        })}
                    </p>
                    <h5>{data.name}</h5>
                </div>
                <div className="quantity-field">
                    <div className="count-item">
                        <div className="item-left">
                            <div className="item-left-top">
                                <IconStarSub color={themeColor} />
                                <p>
                                    {data.point} x {count}
                                </p>
                            </div>
                            <div className="text-small">
                                {t("page.reward.have_points_left", {
                                    returnObjects: true,
                                    number: handleCalculatePoint(),
                                })}
                            </div>
                        </div>
                        <div className="item-right">
                            <button onClick={handleMinusCount} className=" btn-minus">
                                -
                            </button>
                            <p>{count}</p>
                            <button onClick={handleAddCount} className=" btn-add">
                                +
                            </button>
                        </div>
                    </div>
                </div>
                <div className="select-field">
                    <div className="text-small">{t("page.reward.select_type")}</div>
                    <Select
                        //@ts-ignore
                        getPopupContainer={(trigger) => trigger.parentNode}
                        className="select-opts"
                        optionFilterProp="children"
                        defaultActiveFirstOption={true}
                    >
                        {data.variants?.map((item) => (
                            <Option key={item._id} value={item.name}>
                                {item.name}
                            </Option>
                        ))}
                    </Select>
                </div>
                <div className="description-field">
                    <h5>{t("page.reward.descriptions")}</h5>
                    <div>{Parser(data.description)}</div>
                </div>
                <StyledButton
                    size="large"
                    bg_color={themeColor}
                    type="submit"
                    onClick={handleUseCoupon}
                >
                    {t("page.coupon.use_coupon")}
                </StyledButton>
            </div>
        </StyledPreview>
    );
};

const StyledButton = styled(SharedButton)`
    margin-top: 16px;
`;
