import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";

import { selectMerchant } from "@redux";

export const ComponentHeaderAuth = () => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledContainer bg_color={merchant.themeColor} sub_color={merchant.subColor || ""}>
            <div className="image">
                <img src={merchant.logoUrl} />
            </div>
            <div className="content">
                <div className="name">{merchant.name}</div>
                <StyledButton>{t("page.sign_up.membership_details")}&gt;</StyledButton>
            </div>
        </StyledContainer>
    );
};

const StyledContainer = styled.div<{ bg_color: string; sub_color: string }>`
    width: 100%;
    height: 190px;
    background-color: ${(p) => p.bg_color};
    position: relative;
    overflow: hidden;
    .image {
        border-radius: 50%;
        width: 365px;
        height: 365px;
        position: absolute;
        left: 55.35%;
        bottom: -65.83%;
        border: 10px solid ${(p) => p.sub_color};
        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }
    }
    .content {
        position: absolute;
        left: 16px;
        top: 16px;
        .name {
            font-style: normal;
            font-weight: bold;
            font-size: 18px;
            line-height: 24px;
            color: #ffffff;
            margin-bottom: 12px;
        }
    }
`;

const StyledButton = styled.div`
    padding: 4px 16px;
    background: rgba(0,0,0,0.22);
    border-radius: 16px;
    color: #fff;
    font-style: normal;
    font-weight: normal;
    font-size: 13px;
    line-height: 17px;
`;
