import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";
import { useTranslation } from "react-i18next";

import { couponApi } from "@api";
import { ComponentCouponList } from "@components";
import { ICoupon, ICouponList, IPagination } from "@interfaces";
import { setLoading } from "@redux";
import { COUPON, INITIAL_LIST_PARAMS } from "@configs";

interface IProps {
    under: string;
}

export const ModulesCoupon = (props: IProps) => {
    //page hooks
    const dispatch = useDispatch();
    const history = useHistory();
    const { t } = useTranslation();
    //page state
    const [coupons, setCoupons] = useState<ICouponList[] | ICoupon[]>([]);
    const [loadingPagination, setLoadingPagination] = useState<boolean>(false);
    const [param, setParam] = useState<IPagination>(INITIAL_LIST_PARAMS);
    //page props
    const { under } = props;

    useEffect(() => {
        (async () => {
            try {
                dispatch(setLoading(true));
                let res;
                if (under === t("page.home.coupon")) {
                    res = await couponApi.getListCoupon(param);
                    const data = res?.data.docs as ICouponList[];
                    setCoupons(data);
                } else {
                    res = await couponApi.getListCouponMerchant(param);
                    const data = res?.data.docs as ICoupon[];
                    setCoupons(data);
                }
            } catch {
            } finally {
                dispatch(setLoading(false));
            }
        })();
    }, [under]);

    const handleClickItem = (id: string) => {
        if (under === t("page.home.coupon")) {
            const index = coupons.findIndex((coupon) => coupon.qrtable.qrToken === id);
            if (index >= 0) {
                history.push(COUPON + "/" + id + "/" + coupons[index]._id);
            } else {
                history.push(COUPON + "/" + id);
            }
        }
    };

    const handleLoadMore = async () => {
        const paramFake = {
            ...param,
            page: (param.page || 1) + 1,
        };
        setParam(paramFake);
        setLoadingPagination(true);
        const res = await couponApi.getListCoupon(paramFake);
        const data = res.data.docs as ICouponList[];
        setCoupons([...coupons, ...data]);
        setLoadingPagination(false);
    };

    return (
        <>
            {coupons && (
                <ComponentCouponList
                    handleLoadMore={handleLoadMore}
                    data={coupons}
                    handleClickItem={handleClickItem}
                    loadingPagination={loadingPagination}
                />
            )}
        </>
    );
};
