import { useParams } from "react-router";
import { useState } from "react";
import { useDispatch } from "react-redux";

import { ComponentUserVerify } from "@components";
import { enumUseSetting, enumValidation } from "@configs";
import { setLoading } from "@redux";
import { verifyApi } from "@api";
import { useNotify } from "@utils";
import { useTranslation } from "react-i18next";

export const PageVerify = () => {
    //page hooks
    const { type } = useParams<{ type: enumUseSetting }>();
    const dispatch = useDispatch();
    const { error } = useNotify();
    const { t } = useTranslation();
    //page props
    const [otp, setOtp] = useState<string>("");

    const handleChangeOtp = (otp: string) => {
        setOtp(otp);
    };

    const handleSave = async () => {
        if (otp.length < enumValidation.LENTH_OTP) return;

        try {
            dispatch(setLoading(true));
            const res = await verifyApi.verifyEmailCode(otp);
            console.log(res);
        } catch (errors: any) {
            const { message } = errors.response.data;
            if (message) {
                error(message);
            } else {
                error(t("message.update.fail"));
            }
        } finally {
            dispatch(setLoading(false));
        }
    };

    return (
        <ComponentUserVerify
            otp={otp}
            handleChangeOtp={handleChangeOtp}
            type={type}
            handleSave={handleSave}
        />
    );
};
