import { useSelector } from "react-redux";
import { useTranslation } from "react-i18next";
import { useState } from "react";
import styled from "styled-components";

import { ComponentHeaderAuth, ComponentPreviewHome, StyledHome } from "@components";
import { selectAuth, selectMerchant } from "@redux";
import { ModulesCoupon } from "@modules";

export const PageHome = () => {
    //page hook
    const { t } = useTranslation();
    //page redux
    const { userInfo } = useSelector(selectAuth);
    const { themeColor } = useSelector(selectMerchant);
    //page variable
    const navigation: Array<string> = [
        t("page.home.use_point"),
        t("page.home.benefits"),
        t("page.home.coupon"),
        t("page.home.history"),
    ];
    //page state
    const [under, setUnder] = useState<string>(navigation[0]);

    const handleToggleUnder = (values: string) => {
        setUnder(values);
    };

    const render = () => {
        switch (under) {
            case t("page.home.coupon"):
                return <ModulesCoupon />;
                break;
            default:
                return <></>;
                break;
        }
    };

    return (
        <>
            <ComponentHeaderAuth />
            <StyledHome mainColor={themeColor}>
                <ComponentPreviewHome
                    under={under}
                    handleToggleUnder={handleToggleUnder}
                    userInfo={userInfo}
                    navigation={navigation}
                    themeColor={themeColor}
                />
                <StyledContainer>{render()}</StyledContainer>
            </StyledHome>
        </>
    );
};

const StyledContainer = styled.div`
    min-height: 80vh;
    background-color: #f7f7f8;
    width: 88%;
    margin: 0 auto;
`;
