import { useTranslation } from "react-i18next";
import styled from "styled-components";

import { theme } from "@utils";
import { SharedButton } from "@components";

export const ComponentUserVerify = () => {
    const { t } = useTranslation();

    return (
        <StyledSettingSave>
            <StyledSharedButton size="large" bg_color={theme.colors.green} type="submit">
                {t("page.auth.save")}
            </StyledSharedButton>
        </StyledSettingSave>
    );
};

const StyledSettingSave = styled.div`
    text-align: center;
    padding-top: 24px;
`;

const StyledSharedButton = styled(SharedButton)`
    width: 90%;
    position: absolute;
    bottom: 48px;
    left: 5%;
`;
