import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import styled from "styled-components";
import { MailOutlined } from "@ant-design/icons";
import { Checkbox } from "antd";

import { StyledSignUp, SharedInput, SharedButton } from "..";
import { selectMerchant } from "@redux";

export const ComponentPasswordEmail = () => {
    //page hook
    const { t } = useTranslation();
    //page variable
    const merchant = useSelector(selectMerchant);

    return (
        <StyledSignUp>
            <StyledContainer bg_color={merchant.themeColor}>
                <div className="title">
                    {t("page.sign_up.sign_in_to")} {merchant.name}
                </div>
                <div className="choose">{t("page.sign_up.choose_sign_in_method")}</div>
                <SharedInput
                    prefixIcon={<MailOutlined className="site-form-item-icon" />}
                    placeholder={t("page.sign_up.email")}
                />
                <StyledButton size="large" bg_color={merchant.themeColor}>
                    {t("page.sign_up.sign_up")}
                </StyledButton>
                <div className="forgot">
                    {t("page.sign_up.forgot")}
                    <span>{t("page.sign_up.password")}</span>
                </div>
                <div className="remember">
                    <Checkbox>{t("page.sign_up.remember_me")}</Checkbox>
                </div>
            </StyledContainer>
        </StyledSignUp>
    );
};

const StyledContainer = styled.div<{ bg_color: string }>`
    .title {
        font-style: normal;
        font-weight: bold;
        font-size: 20px;
        line-height: 27px;
        color: #000000;
        margin-bottom: 46px;
    }
    .choose {
        font-size: 16px;
        line-height: 21px;
        color: #676767;
        margin-bottom: 42px;
    }
    .site-form-item-icon {
        font-size: 20px;
    }
    .forgot {
        font-size: 16px;
        line-height: 21px;
        font-weight: bold;
        color: #646464;
        text-align: start;
        margin-top: 16px;
        span {
            color: ${(p) => p.bg_color};
        }
    }
    .remember {
        text-align: start;
        font-weight: bold;
        font-size: 16px;
        line-height: 21px;
        margin-top: 8px;
        color: #646464;
    }
`;

const StyledButton = styled(SharedButton)`
    margin-top: 24px;
`;
